mapper.select:
select
count(id)
from coupon where 1=1

  and status = #{status,jdbcType=INTEGER}


  and release_total = #{releaseTotal,jdbcType=INTEGER}



  and name LIKE #{patternName}


   and release_start_date >= #{releaseStartDate,jdbcType=TIMESTAMP}


   and release_end_date <= #{releaseEndDate,jdbcType=TIMESTAMP}


  and coupon_type = #{couponType,jdbcType=INTEGER}


  and scenario_type = #{scenarioType,jdbcType=INTEGER}

select
id, name,  release_total releaseTotal, release_num releaseNum, release_start_date releaseStartDate,
release_end_date releaseEndDate, status
from coupon where 1=1

  and status = #{status,jdbcType=INTEGER}


  and release_total = #{releaseTotal,jdbcType=INTEGER}



  and name LIKE #{patternName}


   and release_start_date >= #{releaseStartDate,jdbcType=TIMESTAMP}


   and release_end_date <= #{releaseEndDate,jdbcType=TIMESTAMP}


  and coupon_type = #{couponType,jdbcType=INTEGER}


  and scenario_type = #{scenarioType,jdbcType=INTEGER}

order by create_date desc
limit #{pageNo},#{pageSize}

select

from coupon
left join aoyi_base_category on aoyi_base_category.category_id = coupon.category
where status = 3


  and aoyi_base_category.category_name LIKE #{patternName}


  and category = #{categoryId,jdbcType=INTEGER}


  and find_in_set(#{tagId,jdbcType=INTEGER}, tags)

limit #{pageNo},#{pageSize}

select count(id) from coupon left join aoyi_base_category on aoyi_base_category.category_id = coupon.category where status = 3
and aoyi_base_category.category_name LIKE #{patternName}
and category = #{categoryId,jdbcType=INTEGER}
and find_in_set(#{tagId,jdbcType=INTEGER}, tags)
