mapper.select:
select

from promotion_mpu
where promotion_id = #{promotionId,jdbcType=INTEGER}
and schedule_id = #{scheduleId,jdbcType=INTEGER}

select
distinct mpu
from promotion_mpu
 where promotion_id = #{promotionId,jdbcType=INTEGER}
and schedule_id = #{scheduleId,jdbcType=INTEGER}

select

from promotion_mpu
where promotion_id = #{promotionId,jdbcType=INTEGER}
and mpu = #{mpu,jdbcType=VARCHAR}
