mapper.select:
select
count(id)
from aoyi_prod_index
where state = 1

  and category = #{category}


  and brand = #{brand}

select
p.id, p.skuId, p.brand,p.category category, p.model, p.name, p.image, p.weight, p.upc,
p.saleUnit, p.state, p.price,p.images_url, p.introduction_url ,e.image imageExtend, e.images_url imagesUrlExtend, e.introduction_url introductionUrlExtend, p.merchant_id, p.inventory, p.brand_id, p.mpu
from aoyi_prod_index p
left join prod_extend e on p.mpu = e.mpu
where state = 1

  and p.category = #{category}


  and p.brand = #{brand}


  order by CAST(price AS DECIMAL) ${priceOrder}

limit #{pageNo},#{pageSize}

select
count(id)
from aoyi_prod_index
where 1 = 1

  and id = #{id}



  and name LIKE #{pattern1}



  and category LIKE #{pattern2}


  and skuid = #{skuid}


  and mpu = #{mpu}



  and aoyi_prod_index.brand LIKE #{pattern4}


  and state = #{state}


  and merchant_id = #{merchantId}

select
p.id, p.skuId, p.brand,p.category category, p.model, p.name, p.image, p.weight, p.upc,p.created_at,p.updated_at,
p.saleUnit, p.state, p.price, p.sprice,p.images_url, p.introduction_url ,e.image imageExtend, e.images_url imagesUrlExtend, e.introduction_url introductionUrlExtend, p.merchant_id, p.inventory, p.brand_id, p.mpu
from aoyi_prod_index p
left join prod_extend e on p.mpu = e.mpu
where 1 = 1

  and p.id = #{id}



  and p.name LIKE #{pattern1}



  and p.category LIKE #{pattern2}


  and p.skuid = #{skuid}


  and p.mpu = #{mpu}



  and p.brand LIKE #{pattern4}


  and p.state = #{state}


  and merchant_id = #{merchantId}


  order by p.created_at asc


  order by p.created_at desc

limit #{pageNo},#{pageSize}

